import React, { Component } from 'react'
import Products from '../../Products'
import Widget from '../../Widget'
import axios from 'axios'

class Shop extends Component {
    constructor(props) {
        super(props)
        this.state = {
            products: [],
            location: {}
        }
    }
    componentDidMount() {
        navigator.geolocation.getCurrentPosition((position) => {
            this.setState({ location: { lat: position.coords.latitude, long: position.coords.longitude } })
        })
        this.fetchData()
    }
    handleSubmit = (e) => {
        e.preventDefault()
        const { products } = this.state
        let data = {
            products
        }
        axios.post('/knitwear', data).then((res) =>
            res.status === 201 ? this.props.history.push('/knitwear') : null
        ).catch(() => { this.setState({ errorMsg: 'There was an error!' }) }
        )
    }
    handleChange = e => this.setState({ [e.target.name]: e.target.value })

    fetchData = async () => {
        const clothes = await axios.get(`https://5dced59675f9360014c2642c.mockapi.io/${this.props.match.params.clothing_name}`)

        this.setState({
            products: [...clothes.data]
        })

    }
    render() {
        { console.log(this.state.location) }
        return (
            <>
                <Products product={this.state.products}
                    onChange={this.handleChange}
                    onSubmit={this.handleSubmit}
                />
                <Widget />
            </>
        )

    }
}
export default Shop
