    constructor(props) {
        super(props)
        this.state = {
            products: [],
            location: {}
        }
    }
    componentDidMount() {
        navigator.geolocation.getCurrentPosition((position) => {
            this.setState({ location: { lat: position.coords.latitude, long: position.coords.longitude } })
