import React, { Component } from 'react'
import Products from '../../Products'
import Widget from '../../Widget'
import axios from 'axios'

class Shop extends Component {
    constructor(props) {
        super(props)
        this.state = {
            products: [],
            location: {},
            reviewText: '',
            review: []
        }
    }
    componentDidMount() {
        navigator.geolocation.getCurrentPosition((position) => {
            this.setState({ location: { lat: position.coords.latitude, long: position.coords.longitude } })
        })
        this.fetchData()
    }

    fetchData = async () => {
        const clothes = await axios.get(`https://5dced59675f9360014c2642c.mockapi.io/${this.props.match.params.clothing_name}`)

        this.setState({
            products: [...clothes.data]
        })

    }
    onSubmit = async (e) => {
        e.preventDefault();
        console.log('hey look for me!!!!')
        let post = await axios.post(`https://5dced59675f9360014c2642c.mockapi.io/${this.props.match.params.clothing_name}`)
            this.setState(state => ({
                review: [...state.list, state.reviewText]
            }))

    }
    onChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }
    render() {
        let review = this.state.review && this.state.review
        { console.log("LOOK AT THIS ONE HERE", this.state.review) }
        return (
            <>
                <Products product={this.state.products}
                />
                <Widget />
                <form onSubmit = {e => this.onSubmit(e)}>
                    <input type= 'text' name='review' onChange={this.onChange}/>
                    {/* <input type="submit" value="Submit" /> */}
                    <button>Submit</button>
                </form>
                {review}
            </>
        )

    }
}
export default Shop
