import React, { Component } from 'react'
import axios from 'axios'

class Shop extends Component {
    constructor(props) {
        super(props)
        this.state = {
            products: [],
            productFilter: ""

        }
    }
    componentDidMount() {
        this.fetchData()
    }

    handleChange = (e) => {
        this.setState({
            productFilter: e.target.value
        })
        this.props.handleChange(e.target.value)
    }

    fetchData = async () => {
        const knitwear = await axios.get('https://5dced59675f9360014c2642c.mockapi.io/knitwear')
        console.log(knitwear)
        this.setState({
            products: knitwear.data[0].clothing
        })
    }

    fetchData = async () => {
        const dresses = await axios.get('https://5dced59675f9360014c2642c.mockapi.io/dresses')
        console.log(dresses)
        this.setState({
            products: dresses.data[0].clothing
        })
    }
    render() {
        const clothes = this.state.products.map((products, index) => (
            <React.Fragment key={index}>
                <div className="shop-container">
                    <h1>Sweaters</h1>
                    <div className="img-container">
                    <img src={products.image} />
                    </div>
                    <div className="description-container">
                    <h2>{products.brand}</h2>
                    <h4>{products.price}</h4>
                    </div>
                </div>

            </React.Fragment>
        ))
        return (
            <div>
            <label htmlFor='filter'>Filter by Category: </label>
            <input type="text" id="filter"
                value={this.state.productFilter}
                onChange={this.handleChange}
                 />
            <div>{ clothes }</div>
            </div>
        )

    }
}
export default Shop
