import React from 'react'
import axios from 'axios'

const IEX_TOKEN = process.env.REACT_APP_IEX_TOKEN

class Widget extends React.Component {
    constructor() {
        super()
        this.state = {
            widget: [],
        }
    }
    componentDidMount() {
        this.fetchData()
    }
    fetchData = async () => {
        const result = await axios.get(`https://www.google.com/maps/embed/v1/MODE?key=${IEX_TOKEN}&parameters`)
        console.log(result)
        this.setState({
            products: result.data[0].widget
        })
    }

}
export default Widget
